import {useState,useEffect} from 'react';
import {Form} from "react-bootstrap";
import {useNavigate,useLocation} from "react-router-dom";
import axios from "axios";



export default function () {
    const navigate = useNavigate();
    const location = useLocation();

    const [state, setState] = useState({
        name : "",
        price : "",
        stockQuantity : "",
        author: "",
        isbn : ""
    });


    const submitForm = (e) => {
        e.preventDefault();
        const formData = new FormData();
        Object.keys(state).map(key => {
            formData.append(key, state[key]);
        })
        axios.post("/items/" + location.state.id + "/edit",formData)
            .then(res => {
                console.log(res);
                if (res.data == true) {
                    alert("book modified");
                    navigate("/");
                } else alert(res.data);
            }).catch(err => {
            alert(err);
        })

    }

    const setInput = (event) => {
        let id = event.target.id;
        let value = event.target.value;
        setState({...state, [id] : value })
    }


    return(
        <Form onSubmit={submitForm}>

            <br/><br/>
            <label>Product Name
                <input id={"name"} type="text" placeholder="type product name" name={"name"} onChange={setInput}/>
            </label>
            <br/><br/>
            <label>Product Price
                <input id={"price"} type="text" placeholder="type product price" name={"price"} onChange={setInput}/>
            </label>
            <br/><br/>

            <label>Stock Quantity
                <input id={"stockQuantity"} type="text" placeholder="type stockQuantity" name={"stockQuantity"} onChange={setInput}/>
            </label>
            <br/><br/>

            <label>author:
                <input id={"author"} type="text" placeholder="type author" name={"author"} onChange={setInput}/>
            </label>

            <label>ISBN:
                <input id={"isbn"} type="text" placeholder="type isbn" name={"isbn"} onChange={setInput}/>
            </label>

            <input type="submit" value={"submit"}/>

        </Form>
    )
}
