import {useState,useEffect} from 'react';
import {Form} from "react-bootstrap";
import {useNavigate} from "react-router-dom";
import axios from "axios";




export default function () {
    const navigate = useNavigate();

    const [memberList,setMemberList] = useState([]);
    const[productList, setProductList] = useState([]);
    const [state, setState] = useState({
        member_id : "",
        product_id : "",
        quantity : ""
    });

    useEffect(() => {
        //memberList
        //productList
        axios.get("/getMember")
            .then(res => setMemberList(res.data))

        axios.get("/getItem")
            .then(res => setProductList(res.data))
    },[])


    const submitForm = (e) => {
        e.preventDefault();
        const formData = new FormData();
        Object.keys(state).map(key => {
            formData.append(key, state[key]);
        })
        axios.post("/orders/new",formData)
            .then(res => {
                console.log(res);
                if (res.data == true) {
                    alert("order created");
                    navigate("/");
                } else alert(res.data);
            }).catch(err => {
            alert(err);
        })

    }

    const setInput = (event) => {
        let id = event.target.id;
        let value = event.target.value;
        setState({...state, [id] : value })
    }


    return(
        <Form onSubmit={submitForm}>



            <br/><br/>
            <Form.Select size="sm" name={"member"} onChange={setInput}>
                <option>select member</option>
                {
                    memberList.map((m,index)=> (
                        <option key = {index} value={m.id}>
                            {m.email}
                        </option>
                    ))
                }
            </Form.Select>


            <br/><br/>
            <Form.Select size="sm" name={"product"} onChange={setInput}>
                <option>select product</option>
                {
                    productList.map((p,index)=> (
                        <option key = {index} value={p.id}>
                            {p.name}
                        </option>
                    ))
                }
            </Form.Select>
            <br/><br/>

            <label>Quantity
                <input id={"quantity"} type="text" placeholder="type quantity" name={"quantity"} onChange={setInput}/>
            </label>
            <br/><br/>

            <input type="submit" value={"submit"}/>

        </Form>
    )
}
