import {useState,useEffect} from 'react';
import {Form} from "react-bootstrap";
import {useNavigate} from "react-router-dom";
import axios from "axios";
import Dropdown from 'react-bootstrap/Dropdown'



export default function () {
    const navigate = useNavigate();

    const [memberList,setMemberList] = useState([]);
    const[productList, setProductList] = useState([]);
    const [state, setState] = useState({
        member_id : "",
        product_id : "",
        quantity : ""
    });

    useEffect(() => {
        //memberList
        //productList
        axios.get("/getMember")
            .then(res => setMemberList(res.data))

        axios.get("/getItem")
            .then(res => setProductList(res.data))
    },[])


    const submitForm = (e) => {
        e.preventDefault();
        const formData = new FormData();
        Object.keys(state).map(key => {
            formData.append(key, state[key]);
        })
        axios.post("/orders/new",formData)
            .then(res => {
                console.log(res);
                if (res.data == true) {
                    alert("order created");
                    navigate("/");
                } else alert(res.data);
            }).catch(err => {
            alert(err);
        })

    }

    const setInput = (event) => {
        let id = event.target.id;
        let value = event.target.value;
        setState({...state, [id] : value })
    }


    return(
        <Form onSubmit={submitForm}>

            <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                    Select Member
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            <br/><br/>
            <label>Select member
                <input id={"member_id"} type="text" placeholder="select member" name={"member"} onChange={setInput}/>
            </label>
            <br/><br/>
            <label>Select product
                <input id={"product_id"} type="text" placeholder="select product" name={"product"} onChange={setInput}/>
            </label>
            <br/><br/>

            <label>Quantity
                <input id={"quantity"} type="text" placeholder="type quantity" name={"quantity"} onChange={setInput}/>
            </label>
            <br/><br/>

            <input type="submit" value={"submit"}/>

        </Form>
    )
}
