import {useState,useEffect} from 'react';
import {Form} from "react-bootstrap";


export default function () {

    const [state, setState] = useState({
        name : "",
        city : "",
        street : "",
        zipcode : ""
    });


    const submitForm = (e) => {
        e.preventDefault();
        const formData = new FormData();
        Object.keys(state).map(key => {
            formData.append(key, state[key]);
        })
        fetch("/members/new",{
            method: "POST",
            body : formData
        }).then((response)=>{
            if (response.ok === true){
                return response.json();
            }
            throw new Error("ERROR");
        }).catch(error => {
            alert(error);
        }).then(data => {

        })

    }
    const setInput = (event) => {
        let id = event.target.id;
        let value = event.target.value;
        setState({...state, [id] : value })
    }


    return(
        <Form onSubmit={submitForm}>
            {console.log(state)}
            <br/><br/>
            <label>name
                <input id={"name"} type="text" placeholder="type your name" name={"name"} onChange={setInput}/>
            </label>
            <br/><br/>
            <label>city
                <input id={"city"} type="text" placeholder="type your city" name={"city"} onChange={setInput}/>
            </label>
            <br/><br/>

            <label>street
                <input id={"street"} type="text" placeholder="type your street" name={"street"} onChange={setInput}/>
            </label>
            <br/><br/>

            <label>zipcode:
                <input id={"zipcode"} type="text" placeholder="type your zipcode" name={"zipcode"} onChange={setInput}/>
            </label>

            <input type="submit" value={"submit"}/>

        </Form>
    )
}
