import logo from './logo.svg';
import './App.css';
import {useEffect, useState} from "react";


function App() {
    const [message, setMessage] = useState([]);
    useEffect(()=> {
        fetch("/hello2")
            .then((res)=>{
                return res.json();
            })
            .then((data)=>{
                setMessage(data);
            })
    },[]);


    return (
        <div className="App">
            <header className="App-header">
            {/*    basic codes  */}
                <ul>
                    {message.map(m => {return <div> {m}</div>})}
                    {message.forEach(m => {return<div> {m}</div>})}
                    {/*{console.log(message)}*/}
                    {/*{message}*/}
                </ul>

            </header>
        </div>
    );
}

export default App;
