import {useEffect, useState} from "react";
import axios from "axios";
import {useLocation} from "react-router-dom";
import {Form} from "react-bootstrap";
import Button from "react-bootstrap/Button";
export default function (props){
    const [orderList, setOrderList] = useState([]);
    const [state, setState] = useState({});
    const [memberList,setMemberList] = useState([]);


    useEffect(()=>{
        axios.get("/members")
            .then(res => setMemberList(res.data))


    },[])

    const setInput = (event) => {

        let name = event.target.name;
        let value = event.target.value;
        setState({...state, [name] : value })
    }

    const requestOrderList = (e) => {
        e.preventDefault();
        axios.get("/order/"+state.member_id)
            .then(res => setOrderList(res.data))
    }

    return (
        <div>Order LIST
            {console.log(state)}
            <Form.Select size="sm" name={"member_id"} onChange={setInput}>
                <option>select member</option>
                {
                    memberList.map((m,index)=> (
                        <option key = {index} value={m.id}>
                            {m.email}
                        </option>
                    ))
                }
            </Form.Select>
            <Button onClick={requestOrderList}>ORDER LIST</Button>
            {console.log(orderList)}

        </div>
    )

}
